mapper.select:
SELECT child_id, parent_id, ancestor_id
FROM t_product
WHERE id = #{id}

SELECT id, child_id, parent_id, ancestor_id, performance_score, salt, item_type, price, image
FROM t_product
WHERE status = 1

    AND item_type LIKE CONCAT('%', #{params.itemType}, '%')


    AND ancestor_id = #{params.ancestorId}


    AND parent_id = #{params.parentId}


    AND child_id = #{params.childId}


    ORDER BY price ${params.sortOrder}

LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*)
FROM t_product
WHERE status = 1

    AND item_type LIKE CONCAT('%', #{params.itemType}, '%')


    AND ancestor_id = #{params.ancestorId}


    AND parent_id = #{params.parentId}


    AND child_id = #{params.childId}

mapper.update:
UPDATE t_product
item_type = #{itemType}, price = #{price}, image = #{image}, salt = #{salt}, performanceScore = #{performanceScore}, modified_user = #{modifiedUser}, modified_time = #{modifiedTime}
WHERE id = #{id}

mapper.select:
SELECT item_type, price FROM t_product WHERE child_id = 111 AND price BETWEEN #{minCPUBudget} AND #{maxCPUBudget}
ORDER BY price ASC, performance_score DESC
ORDER BY price DESC
LIMIT 1;

SELECT item_type, price FROM t_product WHERE (parent_id = 11 ) and (item_type LIKE '%13%' OR item_type LIKE '%14%') AND price BETWEEN #{minCPUBudget} AND #{maxCPUBudget}
ORDER BY price ASC, performance_score DESC
ORDER BY price DESC
LIMIT 1;

SELECT item_type, price FROM t_product WHERE (child_id = 122 OR child_id = 123) AND price BETWEEN #{minCPUBudget} AND #{maxCPUBudget}
ORDER BY price ASC, performance_score DESC
ORDER BY price DESC
LIMIT 1;

SELECT item_type, price FROM t_product WHERE child_id = 121 AND price BETWEEN #{minCPUBudget} AND #{maxCPUBudget}
ORDER BY price ASC, performance_score DESC
ORDER BY price DESC
LIMIT 1;

SELECT item_type, price
FROM t_product
WHERE parent_id = #{parentId}
AND child_id = #{childId}
AND (item_type LIKE '%16g%' OR item_type LIKE '%16GB%'
OR item_type LIKE '%24g%' OR item_type LIKE '%24GB%')
AND price BETWEEN #{minGPUBudget} AND #{maxGPUBudget}


        ORDER BY price ASC, performance_score DESC


        ORDER BY price DESC


LIMIT 1

SELECT item_type, price
FROM t_product
WHERE parent_id = #{parentId}
AND (
(item_type LIKE '%B660%' OR item_type LIKE '%B760%' OR item_type LIKE '%Z690%'
OR item_type LIKE '%Z790%' OR item_type LIKE '%H610%')
AND item_type NOT LIKE '%B650%' -- 排除含有 B650 的项
)
AND child_id = #{childId}
AND price BETWEEN #{minMotherboardBudget} AND #{maxMotherboardBudget}


        ORDER BY price ASC, performance_score DESC


        ORDER BY price DESC


LIMIT 1

SELECT item_type, price
FROM t_product
WHERE parent_id = #{parentId}
AND (item_type LIKE '%B450%' OR item_type LIKE '%B550%' OR item_type LIKE '%B650%'
OR item_type LIKE '%A520%')
AND child_id = #{childId}
AND price BETWEEN #{minMotherboardBudget} AND #{maxMotherboardBudget}


        ORDER BY price ASC, performance_score DESC


        ORDER BY price DESC


LIMIT 1

SELECT item_type, price
FROM t_product
WHERE ancestor_id = 8
AND parent_id = #{parentId}
AND child_id = #{childId}
AND price BETWEEN #{minCaseBudget} AND #{maxCaseBudget}


        ORDER BY price ASC, performance_score DESC


        ORDER BY price DESC


LIMIT 1

SELECT item_type, price
FROM t_product
WHERE (item_type like CONCAT('%', #{supplySize}, '%'))
AND parent_id = #{parentId}
AND child_id = #{childId}
AND price BETWEEN #{minSUEBudget} AND #{maxSUEBudget}


        ORDER BY price ASC, performance_score DESC


        ORDER BY price DESC


LIMIT 1

SELECT item_type, price
FROM t_product
WHERE parent_id = #{parentId}

    AND child_id = #{childId}


    AND item_type LIKE CONCAT('%', #{memorySize}, '%')


    AND price >= #{minMemoryBudget}


    AND price <= #{maxMemoryBudget}



        ORDER BY price ASC,performance_score DESC


        ORDER BY price DESC


LIMIT 1

SELECT item_type, price
FROM t_product
WHERE parent_id = #{parentId}
AND child_id = #{childId}
AND price BETWEEN #{minCoolingBudget} AND #{maxCoolingBudget}


        ORDER BY price ASC,performance_score DESC


        ORDER BY price DESC


LIMIT 1
